package ayds.dictionary.bravo.Model;class ArticleModelImp implements ArticleModel {    private final Repository repository;    private Article article;    private ArticleModelListener listener;    ArticleModelImp(Repository repository){        this.repository=repository;    }    public Article getArticle(){        return article;    }    public void setListener(ArticleModelListener listener){        this.listener=listener;    }    public void searchArticle(String term){        article=repository.getArticle(term);        notifyListener();    }    private void notifyListener(){        if(listener!=null){            listener.didUpdateArticle();        }    }}