package ayds.dictionary.bravo.Model;import ayds.dictionary.bravo.Model.ErrorHandler.ErrorHandler;import java.util.List;class ArticleModelImp implements ArticleModel {    private final Repository repository;    private List<Article> articles;    private ErrorHandler errorHandler;    private ArticleModelListener listener;    ArticleModelImp(Repository repository, ErrorHandler errorHandler) {        this.errorHandler = errorHandler;        this.repository = repository;    }    public List<Article> getArticles() {        return articles;    }    public void setErrorListener(ErrorHandlerListener listener) {        errorHandler.setErrorListener(listener);    }    public void setListener(ArticleModelListener listener) {        this.listener = listener;    }    public void searchArticles(String term) {        articles = repository.getArticles(term);        notifyListener();    }    private void notifyListener() {        if (listener != null) {            listener.didUpdateArticles();        }    }}