package ayds.dictionary.bravo.Model;import ayds.dictionary.bravo.Model.ErrorHandler.ErrorHandler;import ayds.dictionary.bravo.Model.LocalSource.LocalSource;import ayds.dictionary.bravo.Model.RemoteSource.WikipediaAdapter;import java.io.IOException;public class Repository {    private final LocalSource localSource;    private final WikipediaAdapter wikipediaAdapter;    private final ErrorHandler errorHandler;    public Repository(LocalSource localSource, WikipediaAdapter wikipediaAdapter, ErrorHandler errorHandler) {        this.localSource = localSource;        this.wikipediaAdapter = wikipediaAdapter;        this.errorHandler = errorHandler;    }    public Article getArticle(String term) {        try {            Article article = localSource.getMeaning(term);            if (article == null) {                article = wikipediaAdapter.getArticle(term);                localSource.saveArticle(article);            } else {                article.addPrefix();            }            return article;        } catch (IOException e) {            errorHandler.hasError(new Exception("No hay conexi√≥n."));            return new Article(term, null, Source.WIKIPEDIA);        }    }}