package ayds.dictionary.bravo.Model;import ayds.dictionary.bravo.Model.LocalSource.LocalSource;public class Repository {    private final LocalSource localSource;    private final WikipediaAdapter wikipediaAdapter;    public Repository(LocalSource localSource, WikipediaAdapter wikipediaAdapter) {        this.localSource = localSource;        this.wikipediaAdapter = wikipediaAdapter;    }    public Article getArticle(String term) {        Article article = localSource.getMeaning(term);        if (article == null) {            article = wikipediaAdapter.getArticle(term);            localSource.saveArticle(article);        } else {            article.addPrefix();        }        return article;    }}