package ayds.dictionary.bravo.Model;import ayds.dictionary.bravo.Model.LocalSource.LocalSource;import ayds.dictionary.bravo.Model.RemoteSource.RemoteSource;import java.io.IOException;import java.sql.SQLException;public class Repository {    private final LocalSource localSource;    private final RemoteSource remoteSource;    private final ErrorHandler errorHandler;    public Repository(LocalSource localSource, RemoteSource remoteSource, ErrorHandler errorHandler) {        this.localSource = localSource;        this.remoteSource = remoteSource;        this.errorHandler =errorHandler;    }    public Article getArticle(String term) {        Article article = localSource.getMeaning(term);            if (article == null) {                article = remoteSource.getMeaning(term);                localSource.saveArticle(article);            } else {                article.addPrefix();            }        return article;    }}