package ayds.dictionary.bravo.View;class TextConverter {    private final static String font = "arial";    public static String textToHtml(String term, String text) {        StringBuilder builder = new StringBuilder();        appendHeader(builder);        String textWithBold = replace(text, term);        builder.append(textWithBold);        appendFooter(builder);        return builder.toString();    }    private static void appendHeader(StringBuilder builder) {        builder.append("<font face=\"" + font + "\">");    }    private static String replace(String text, String term) {        String textReplaced = text.replaceAll("(?i) " + term, " <b>" + term + "</b>");        return textReplaced;    }    private static void appendFooter(StringBuilder builder) {        builder.append("</font>");    }}