package ayds.dictionary.bravo.View;class TextConverter {    private final static String font = "arial";    public static String textToHtml(String term, String text) {        StringBuilder builder = new StringBuilder();        appendHeader(builder);        String textWithBold = replace(text, term);        builder.append(textWithBold);        appendFooter(builder);        return builder.toString();    }    private static void appendHeader(StringBuilder builder) {        builder.append("<font face=\"" + font + "\">");    }    private static String replace(String text, String term) {        String initialCapitalCharacter= term.substring(0,1).toUpperCase();        String endTerm= term.substring(1);        String termCapitalIni=initialCapitalCharacter.concat(endTerm);        String termAllCapital=term.toUpperCase();        String textReplaced = text                .replace( term, " <b>" + term + "</b>")                .replace( termCapitalIni, " <b>" + termCapitalIni + "</b>")                .replace( termAllCapital, " <b>" + termAllCapital + "</b>");        return textReplaced;    }    private static void appendFooter(StringBuilder builder) {        builder.append("</font>");    }}