package ayds.dictionary.bravo.View;class TextConverter {    private final static String font = "arial";    private final static String successSourceHeader = "<b> <h3 style=\"background-color:#00FA9A;\">";    private final static String successSourceFooter = ": </h3> </b>";    private final static String noResultSourceHeader = " <b><h3 style=\"background-color:#F08080;\">";    private final static String noResultSourceFooter = ": </h3> </b>";    public static String textToHtml(String term, String text) {        StringBuilder builder = new StringBuilder();        appendHeader(builder);        String textWithBold = replace(text, term);        builder.append(textWithBold);        appendFooter(builder);        return builder.toString();    }    private static void appendHeader(StringBuilder builder) {        builder.append("<font face=\"" + font + "\">");    }    private static String replace(String text, String term) {        String initialCapitalCharacter= term.substring(0,1).toUpperCase();        String endTerm= term.substring(1);        String termCapitalIni=initialCapitalCharacter.concat(endTerm);        String termAllCapital=term.toUpperCase();        String textReplaced = text                .replace( term, " <b>" + term + "</b>")                .replace( termCapitalIni, " <b>" + termCapitalIni + "</b>")                .replace( termAllCapital, " <b>" + termAllCapital + "</b>");        return textReplaced;    }    private static void appendFooter(StringBuilder builder) {        builder.append("</font>");    }    public static String setSuccessSource(String source){        return successSourceHeader + source + successSourceFooter;    }    public static String setNoResultSource(String source){        return noResultSourceHeader + source + noResultSourceFooter;    }}