package ayds.dictionary.bravo.fulllogic.Modelo;import ayds.dictionary.bravo.fulllogic.Modelo.LocalSource.LocalSource;import ayds.dictionary.bravo.fulllogic.Modelo.LocalSource.LocalSourceImp;import ayds.dictionary.bravo.fulllogic.Modelo.RemoteSource.RemoteSource;import ayds.dictionary.bravo.fulllogic.Modelo.RemoteSource.RemoteSourceImp;public class ArticleModelImp implements ArticleModel {    private final Repository repository;    private Article article;    private ArticleModelListener listener;    public ArticleModelImp(Repository repository){        this.repository=repository;    }    public Article getArticle(){        return article;    }    public void setListener(ArticleModelListener listener){        this.listener=listener;    }    public void updateArticle(String term){        article=repository.getArticle(term);        notifyListener();    }    private void notifyListener(){        if(listener!=null){            listener.didUpdateArticle();        }    }}